# /usr/bin/env python# -*- coding: utf-8 -*-from __future__ import unicode_literals, absolute_importimport tracebackimport csvimport xlrdimport sysimport jsonimport requestsimport codecsVMINFO_URL = 'http://10.13.2.133:6101/v1/resop/vm/vm_info/'GET_CUSTOMER_BYNO_URL = 'http://10.13.2.235:6003/customer/getCustomerByNo'#reload(sys)#sys.setdefaultencoding('utf-8')def read_from_xlsx(file_name):    workbook_xlsx = xlrd.open_workbook('%s' % file_name,'r')    sheetnames = workbook_xlsx.sheet_names()    sheet = workbook_xlsx.sheet_by_name(sheet_name=sheetnames[0])    msg = []    for row in range(sheet.nrows):        msg.append(sheet.cell(row, 0).value)    return msgdef get_customer_info(vm_name):    """        根据vm名称手动截取‘-’前的第一个字段为客户编号,并根据客户编号,查询客户信息    :return: customer info values 客户信息    """    cst_values = {}    customer_no = vm_name.split('-')[0]    headers = {'Content-Type': 'application/json'}    csm_data = {"customerNo": customer_no}    post_csm_data = json.dumps(csm_data)#    logger.info("Get customer info by customerNo start...")    response = requests.post(url=GET_CUSTOMER_BYNO_URL,                             data=post_csm_data, headers=headers).json()#    logger.info("Get customer info by customerNo end...")    cst_infos = response.get('customer')    cst_values['customer_name'] = cst_infos.get('customerName')    cst_values['customer_contacts'] = cst_infos.get('operateContact')    cst_values['customer_mobile'] = cst_infos.get('contactPhone')    cst_values['customer_email'] = cst_infos.get('contactEmail')#    logger.info("customer info is %s" % cst_values)    return cst_valuesdef get_vminfo_byname(vm_name):    """        根据vm名称, 从api接口获取相关信息    :return: vm info data:e.g.: {'host': 'xxx'}    """    headers = {'Content-Type': 'application/json'}    data = {"vm_name": vm_name}    post_data = json.dumps(data)    results = requests.post(        url=VMINFO_URL, headers=headers, data=post_data).json()#    logger.info('VM信息获取结果:%s' % results.get("code_msg"))    if results.get("code_msg") == "success":        vm_data = results.get('data')        vm_data['comment'] = 'ok'        return vm_data    # 数据异常时,只返回客户信息    message = results.get("message")    vm_data = get_customer_info(vm_name)    vm_data['comment'] = message#    logger.warn('VM:%s只返回客户信息' % vm_name)    return vm_data#    return results.get('data')if __name__ == '__main__':    filename = 'test01.xlsx'    vm_name = read_from_xlsx(filename)    for i in vm_name:        vm_data_msg = get_vminfo_byname(i)        print (vm_data_msg)